  render(){      //div er bodole amra emn fragment o use korte parbo
  return <>
            <h1>My name is {this.props.name}</h1>   
            <h1>My id is {this.props.id}</h1>
  </>
  }
}

Employee.propTypes={
  name: pt.string.isRequired,    //prop types npm i prop-types
  id: pt.number.isRequired
}
  */


 //State use kori jno pros er value change korte pari
 class Employee extends Component{
   constructor(props){
      super(props);
           this.state={
             name:this.props.name,
           };
   }
   render(){
     return(
       <>
       <h1>Hello world my name is {this.state.name}</h1>
     <h1>Total click {this.state.count}</h1>

       </>
     )
   }
  }

ReactDOM.render(<Employee  name="shakib" id={12}/>,document.getElementById("root"));
