import React,{Component} from 'react';



 class EmployeeB extends Component{

   constructor(props){
      super(props);
           this.state={
             name:this.props.name,
             id:this.props.id
            
           };
           console.log("Cons calling");
   }

   static getDerivedStateFromProps(prop, state){
    console.log("getDerivedStateFromProps");
    return null;
   }

   updateId=()=>{
     let emp_id=this.state.id;
     this.setState({id:emp_id++});
   }
   render(){
     return(
       <>
       <h1>Hello world A</h1>
     <h1>your id: {this.state.id}</h1>
     <button onClick={this.updateId}>Update id</button>
   
       </>
     )
   }
  }

  export default EmployeeB;
